    region,
    COUNT(*) as centers,
    ROUND(AVG(latitude)::numeric, 4) as avg_lat,
    ROUND(AVG(longitude)::numeric, 4) as avg_lng
FROM test_centers 
GROUP BY region 
ORDER BY centers DESC;
